import{_ as i,c as t,o as d,ag as e}from "./chunks/framework.BVgKs9HZ.js";const c=JSON.parse('{"title":"Android Studio的安装和测试","description":"android learning 1.","frontmatter":{"title":"Android Studio的安装和测试","date":"2017-09-07T20:00:50.000Z","tags":["一日一记"],"description":"android learning 1."},"headers":[],"relativePath":"post/diary/2016-2017/2017-09-07.md","filePath":"post/diary/2016-2017/2017-09-07.md"}'),o={name:"post/diary/2016-2017/2017-09-07.md"};function r(n, a, l, s, u, _){return d(),t("div",null,a[0]||(a[0]=[e('<h2 id="android-studio安装" tabindex="-1">android studio安装 <a class="header-anchor" href="#android-studio安装" aria-label="Permalink to &quot;android studio安装&quot;">​</a></h2><ul><li>安装jdk和jre,设置环境变量，cmd 通过java -version 指令来验证是否安装成功。</li><li>下载android studio 集成版本，设置好sdk和avd的目录，进行安装。</li><li>在不能够实现avd的时候，可以采用genymotion模拟器，或采用真机来进行模拟。建议采用真机,因为这样比较快,如果采用真机的话,就需要进行安装驱动,再调用adb进行安装apk. 参考文献：<a href="http://ask.android-studio.org/?/question/789" target="_blank" rel="noreferrer">http://ask.android-studio.org/?/question/789</a></li></ul><h2 id="开发环境介绍" tabindex="-1">开发环境介绍 <a class="header-anchor" href="#开发环境介绍" aria-label="Permalink to &quot;开发环境介绍&quot;">​</a></h2><ul><li>参考《第一行代码 第2版》--郭霖第一章，认识各个部分的含义，主要是app和gradle部分，能够找到常用切换的位置，能够配置项目的基本参数，会使用日志工具log输出我们想看到的数据， 这个和eclipse里面的开发很像，转换过来也是极为方便的。</li><li>不同点在于gradle的使用，需要特意注意这方面知识的学习。</li></ul>',4)]))}const h=i(o,[["render",r]]);export{c as __pageData,h as default};
